CREATE TABLE admin_users (id BIGINT UNSIGNED AUTO_INCREMENT, email VARCHAR(255) NOT NULL, password VARCHAR(40) NOT NULL, password_salt VARCHAR(40) NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = INNODB;
CREATE TABLE trackings (id VARCHAR(255), name VARCHAR(255) NOT NULL, system_name VARCHAR(255) NOT NULL, model VARCHAR(255) NOT NULL, system_version VARCHAR(255) NOT NULL, localized_model VARCHAR(255) NOT NULL, locale VARCHAR(10) NOT NULL, network VARCHAR(255) NOT NULL, launch BIGINT NOT NULL, version VARCHAR(20) NOT NULL, last_at DATETIME, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = INNODB;
